import { useState, useEffect } from 'react';
import React,{ Component , Fragment } from 'react';
function TodoList() {
    //const [count, setCount] = useState(0)
    const [inputValue,inputChange] = useState('')
    const [list, listchange] = useState([])

  
    /* useEffect(() => {
      document.title = `counterWithHook ${count}`
    }, [count]) */
  
    /* useEffect(() => {
      console.log('you name is', name)
    }, [name]) */
  
    return (
      <Fragment>
        {/* <p>You click {count} times</p>
        <button onClick={e => setCount(count => count + 1)}>Click me</button> */}
            <input onChange={e => inputChange(e.target.value)} />
            <button onClick={() => listchange(list.push(inputValue))}>Submit</button>
            <ul>
                {{list}.map((value,index) => {
                return (
                    <div key={index}>
                    <li>{value}</li>
                    </div>
                )
                })}
            </ul>
      </Fragment>
    )
  }
export default TodoList;
