import { useState, useEffect } from 'react';
import React,{ Component , Fragment } from 'react';
function TodoList() {
    const [inputValue,inputChange] = useState('')
    const [data, listchange] = useState({list:['qwe','wqe']})
    return (
      <Fragment>
            <input onChange={e => inputChange(e.target.value)} />
            <button onClick={() => listchange(inputValue => {return [...data.list,inputValue]})}>Submit</button>
            {/* <ul>
                {data.list.map((value,index) => {
                    return (
                        <div key={index}>
                            <li>{value}</li>
                        </div>
                    )
                })}
            </ul> */}
            <div>{data.list}</div>
      </Fragment>
    )
  }
export default TodoList;
